/** * * Operadores Aritméticos * @author Edy Segura, edy@segura.pro.br * */import java.util.Scanner;public class OperadoresAritmeticos {	public static void main(String[] args) {		int a, b, result;		Scanner s = new Scanner(System.in);				System.out.print("\nInforme o valor de A: ");		a = s.nextInt();				System.out.print("Informe o valor de B: ");		b = s.nextInt();				result = a + b;		System.out.println("\nA + B = " + result);				result = a - b;		System.out.println("A - B = " + result);				result = a * b;		System.out.println("A * B = " + result);				result = a / b;		System.out.println("A / B = " + result);				result = a % b;		System.out.println("A % B = " + result);				System.out.println("A = " + a + " e B = " + b);		System.out.println("-A = " + (-a));		System.out.println("++A = " + (++a));		System.out.println("B-- = " + (b--));		System.out.println("A = " + a + " e B = " + b);	}}